mapper.select:
SELECT userId, password, userName, telno, point, createdAt, updatedAt, birtyD
FROM users
WHERE userId = #{userId} AND password = #{userPw}

SELECT  userId, password, userName, telno, point, createdAt, updatedAt, birtyD
FROM users
WHERE userId = #{userId}

SELECT sp.seatPriceId, bs.ticketId, u.userName AS userName, bs.ticketName AS ticketName, bs.ticketType AS ticketType, sp.status as status, sp.usedDt AS usedDt FROM seatprice sp JOIN Users u ON sp.userId = u.userId JOIN book_show bs ON sp.ticketId = bs.ticketId WHERE 1=1 AND sp.usedDt BETWEEN #{startDate} AND #{endDate} AND u.userId = #{userId}
AND bs.ticketType = #{ticketType}
GROUP BY sp.ticketId, u.userId, bs.placeId

mapper.update:
UPDATE users
SET
    updatedAt = sysdate()

        , password = #{userPw}


        , userName = #{userName}

WHERE userId = #{userId}
