mapper.select:
SELECT  count(*)
FROM users
WHERE userId = #{userId}

mapper.insert:
INSERT INTO users(userId, password, userName, telno, point, createdAt, updatedAt, birtyD)
VALUES(
        #{userId}
      , #{userPw}
      , #{userName}
      , #{telNo}
      , 0
      , sysdate()
      , sysdate()
      , #{birtyD}
  )

mapper.select:
SELECT userId, password, userName, telno, point, createdAt, updatedAt, birtyD
FROM users
WHERE userId = #{userId} AND password = #{userPw}

SELECT  userId, password, userName, telno, point, createdAt, updatedAt, birtyD
FROM users
WHERE userId = #{userId}

SELECT sp.seatPriceId, bs.ticketId, sp.placeId, u.userName, bs.ticketName, bs.ticketType, sp.status, sp.usedDt, sp.createAt FROM seatprice sp JOIN Users u ON sp.userId = u.userId JOIN book_show bs ON sp.ticketId = bs.ticketId WHERE 1=1 AND sp.createAt BETWEEN #{startDate} AND DATE_ADD(#{endDate}, INTERVAL 1 DAY) AND u.userId = #{userId}
AND bs.ticketType = #{ticketType}
GROUP BY sp.ticketId, u.userId, bs.placeId

mapper.update:
UPDATE seatprice
SET
    status = 'cancle'
WHERE userId = #{userId}
    and ticketId = #{ticketId}
    and placeId = #{placeId}

UPDATE users
SET
updatedAt = sysdate()

    , password = #{userPw}


    , userName = #{userName}

WHERE userId = #{userId}

UPDATE users
SET point = #{point}
WHERE userId = #{userId}
