  )

mapper.select:
SELECT userId, password, userName, telno, point, createdAt, updatedAt, birtyD
FROM users
WHERE userId = #{userId} AND password = #{userPw}

SELECT  userId, password, userName, telno, point, createdAt, updatedAt, birtyD
FROM users
WHERE userId = #{userId}

mapper.update:
UPDATE users
SET
    updatedAt = sysdate()

        , password = #{userPw}


        , userName = #{userName}

WHERE userId = #{userId}
