mapper.select:
SELECT  count(*)
FROM usertable
WHERE userId = #{userId}

SELECT userseq, userId, userPwd, userName, userPhoneNumber, userPoint, createdAt, MDF_DT, userBirty
FROM usertable
WHERE userId = #{userId} AND userPwd = #{userPwd}

SELECT  userId, userPwd, userName, userPhoneNumber, userPoint, createdAt, MDF_DT, userBirty
FROM usertable
WHERE userId = #{userId}

mapper.update:
UPDATE usertable
SET
    MDF_DT = sysdate()

    , userPwd = #{userPwd}


    , userName = #{userName}

WHERE userId = #{userId}

UPDATE usertable
SET userPoint = #{userPoint}
WHERE userId = #{userId}
